import React, { useState, useEffect } from 'react'

import '../App.css'


const TimeAndDate = () => {

    // Manage date and time.
    let fullDate = new Date()

    const time = fullDate.toLocaleTimeString({ hour: 'numeric', hour12: false, minute: 'numeric' })
    let currentHour = fullDate.getHours()
    let currentMinutes = fullDate.getMinutes()
    let currentSeconds = fullDate.getSeconds()

    const [hour, setHour] = useState(currentHour)
    const [minutes, setMinutes] = useState(currentMinutes)
    const [seconds, setSeconds] = useState(currentSeconds)

    useEffect(() => {
        const timer = setTimeout(() => {
            setSeconds((seconds) => seconds + 1)
            setMinutes((minutes) => minutes + 1)
            setMinutes((hour) => hour + 1)
        }, 1000)
        return () => {
            clearInterval(timer)
        }
    })


    return (
        <div className='time-and-date'>
            <div className='time'>
                {hour}:{minutes}
            </div>
            <div className='date'>
                Saturday | October 13
            </div>
        </div>
    )
}

export default TimeAndDate
